import { combineReducers } from 'redux'

const calculatorReducer = (currentValue = 0, action) => {
    if (action.type === 'ADD_ONE') {
        currentValue = currentValue + 1;
        return currentValue
    }
    if (action.type === 'SUBTRACT_ONE') {
        currentValue = currentValue - 1;
        return currentValue
    }
    if (action.type === 'ADD_SOMETHING') {
        currentValue = currentValue + action.payload;
        return currentValue
    }

    return currentValue
}

const oldhistory = (historyList = [], action) => {
    if (action.type === 'ADD_ONE') {
        historyList.push('adding one')
        return historyList
    }
    if (action.type === 'SUBTRACT_ONE') {
        historyList.push('subtracting one')
        return historyList
    }
    if (action.type === 'ADD_SOMETHING') {
        historyList.push('adding any number')
        return historyList
    }
    if (action.type === 'CLEAR_HISTORY') {
        historyList = []
        return historyList
    }

    return historyList
}

const history = (historyList = '', action) => {
    if (action.type === 'ADD_ONE') {
        historyList = 'adding one'
        return historyList
    }
    if (action.type === 'SUBTRACT_ONE') {
        historyList = 'subtracting one'
        return historyList
    }
    if (action.type === 'ADD_SOMETHING') {
        historyList = 'adding any number'
        return historyList
    }
    if (action.type === 'CLEAR_HISTORY') {
        historyList = ''
        return historyList
    }

    return historyList
}

export default combineReducers({calculatorReducer: calculatorReducer, histoyReducer: history})
