import React from 'react'
import { connect } from 'react-redux'
import { addActionCreator, subtractActionCreator, addAnyActionCreator } from '../actions'

class Calculator extends React.Component {
    render() {
        return (
            <div>Calculator</div>
        )
    }
}

const mapStateToProps = state => {
    return {currentValue: state.calculatorReducer}
}

export default connect(mapStateToProps, {clearHistoryActionCreator: clearHistoryActionCreator})(Calculator)
